fn my_first_interpreter(code: &str) -> String {
    let command_characters: String = code
        .chars()
        .filter(|&x| (x == '+') | (x == '.')).collect();
    let result: Vec<u8> = command_characters
        .split('.')
        .map(|x| x.len())
        .scan(0, |state, el| {
            *state += el;
            Some(*state as u8)
        }).collect();
    let c = result.len() - 1;
    result.into_iter().map(|x| x as char).take(c).collect()
}

